using UnityEngine;using System.Collections;using System.Diagnostics;public class CircleRotateScript : MonoBehaviour {    [SerializeField]    private float rotationSpeed = 70f;    private bool canRotate;    private float angle;	// Use this for initialization	void Awake () {        canRotate = true;        StartCoroutine(ChangeRotation());		}		// Update is called once per frame  	void Update () {        if (canRotate)        {            RotateTheCircle();        }		}    IEnumerator ChangeRotation ()    {        yield return new WaitForSeconds(1f);        if (Random.Range(0, 2) > 0)        {            rotationSpeed = -Random.Range(50, 100);        }        else        {            rotationSpeed = Random.Range(50, 100);        }        StartCoroutine(ChangeRotation());                }    void RotateTheCircle ()    {        angle = transform.rotation.eulerAngles.z;        angle += rotationSpeed * Time.deltaTime;        transform.rotation = Quaternion.Euler(new Vector3(0, 0, angle));    }}