using UnityEngine;using System.Collections;using UnityEngine.UI;using UnityEngine.SceneManagement;using UnityEngine.SocialPlatforms.Impl;public class GameOverManager : MonoBehaviour {    public static GameOverManager instance;    private GameObject gameOverPanel;    private Animator gameOverAnim;    private Button playAgainButton, backButton;    private GameObject scoreText;    private Text finalScore;    void Awake ()    {        MakeInstance();        InitializeVariable();    }    void MakeInstance ()    {        if (instance == null)        {            instance = this;        }    }    void InitializeVariable ()    {        gameOverPanel = GameObject.Find("Game Over Panel Holder");        gameOverAnim = gameOverPanel.GetComponent<Animator>();        playAgainButton = GameObject.Find("Play Again Button").GetComponent<Button>();        playAgainButton.onClick.AddListener(()=> PlayAgain());        backButton = GameObject.Find("Back Button").GetComponent<Button>();        backButton.onClick.AddListener(()=> BackButton());        finalScore = GameObject.Find("Score Text").GetComponent<Text>();        scoreText = GameObject.Find("Score");        gameOverPanel.SetActive(false);                }    public void GameOverShowPanel ()    {        scoreText.SetActive(false);        gameOverPanel.SetActive(true);        finalScore.text = "Score: " + ScoreManager.instance.GetSocre();         gameOverAnim.Play("FadeIn");    }    public void PlayAgain ()    {        SceneManager.LoadScene("GamePlay");    }    public void BackButton ()    {        SceneManager.LoadScene("MainMenu");    }	}