using UnityEngine;using System.Collections;using UnityEngine.UI;public class ScoreManager : MonoBehaviour {    public static ScoreManager instance;    private Text scoreText;    private int score;	// Use this for initialization	void Awake () {        scoreText = GameObject.Find("Score").GetComponent<Text>();        MakeInstance();		}    public void IncrementScore ()    {        score++;        scoreText.text = "" + score;    }    void MakeInstance ()    {        if (instance == null)        {            instance = this;                   }    }    public int GetSocre ()    {        return this.score;    }		// Update is called once per frame	void Update () {		}}