using UnityEngine;using System.Collections;public class BallScript : MonoBehaviour {    private int touchedGround = 0;    private bool touchRam;    void OnCollisionEnter2D (Collision2D target)    {        if (target.gameObject.tag == "Holder")        {            if (GameManager.instance != null)            {                if (Random.Range(0, 2) > 1)                {                    GameManager.instance.PlaySource(3);                }                else                {                    GameManager.instance.PlaySource(4);                }            }                    }        if (target.gameObject.tag == "Ram")        {            touchRam = true;            if (GameManager.instance != null)            {                if (Random.Range(0, 2) > 1)                {                    GameManager.instance.PlaySource(2);                }                else                {                    GameManager.instance.PlaySource(5);                }            }                    }        if (target.gameObject.tag == "Ground")        {            touchedGround++;            if (touchedGround < 3)            {                if (GameManager.instance != null)                {                    if (Random.Range(0, 2) > 1)                    {                        GameManager.instance.PlaySource(3);                    }                    else                    {                        GameManager.instance.PlaySource(4);                    }                }            }        }        if (target.gameObject.tag == "Table")        {            touchRam = true;            if (GameManager.instance != null)            {                if (Random.Range(0, 2) > 1)                {                    GameManager.instance.PlaySource(2);                }                else                {                    GameManager.instance.PlaySource(5);                }            }        }            }    void OnTriggerEnter2D (Collider2D target)    {        if (target.tag == "Net")        {            if (GameManager.instance != null)            {                GameManager.instance.PlaySource(1);                if (touchRam)                {                    GameManager.instance.IncrementBalls(1);                }                else                {                    GameManager.instance.IncrementBalls(2);                }            }        }    }}