using UnityEngine;using System.Collections;using UnityEngine.UI;using System.Collections.Generic;public class BallSelectScript : MonoBehaviour {    private List <Button> buttons = new List<Button>();	// Use this for initialization	void Awake () {        GetButtonsAndAddListener();	}		// Update is called once per frame	void Update () {		}    void GetButtonsAndAddListener ()    {        GameObject[] btns = GameObject.FindGameObjectsWithTag("MenuBalls");        for (int i = 0; i < btns.Length; i++)        {            buttons.Add(btns[i].GetComponent<Button>());            buttons[i].onClick.AddListener(() => SelectABall());        }            }    void SelectABall ()    {        int index = int.Parse(UnityEngine.EventSystems.EventSystem.current.currentSelectedGameObject.name);        //Inform to gama manager wich index is select        if (GameManager.instance != null)        {            GameManager.instance.SetBallIndex(index);        }    }}