using UnityEngine;using System.Collections;using UnityEngine.SceneManagement;public class MainMenuController : MonoBehaviour {    private Animator mainAnim, ballsAnim;	// Use this for initialization	void Awake () {        mainAnim = GameObject.Find("Main Menu Buttons Holder").GetComponent<Animator>();        ballsAnim = GameObject.Find("Select Ball Holder").GetComponent<Animator>();	}    public void PlayGame ()    {        SceneManager.LoadScene("GamePlay");    }    public void SelectBalls ()    {        mainAnim.Play("FadeOut");        ballsAnim.Play("FadeIn");    }    public void BackToMenu ()    {        mainAnim.Play("FadeIn");        ballsAnim.Play("FadeOut");    }		// Update is called once per frame	void Update () {		}}