using UnityEngine;using System.Collections;using System.Xml.Serialization;using UnityEngine.SceneManagement;public class GameManager : MonoBehaviour {    public static GameManager instance;    private BallCreator ballCreator;    private int index = 0;	// Use this for initialization	void Awake () {        MakeSingleton();        ballCreator = GetComponent<BallCreator>();		}    void MakeSingleton ()    {        if (instance == null)        {            instance = this;            DontDestroyOnLoad(gameObject);        }        else        {            Destroy(gameObject);        }    }	    public void SetBallIndex (int index)    {        this.index = index;    }    void CreateBall (int index)    {        ballCreator.CreateBall(index);    }    void OnLevelWasLoaded ()    {        if (SceneManager.GetActiveScene().name == "GamePlay")        {            CreateBall(index);        }    }}