using UnityEngine;using System.Collections;using UnityEngine.SceneManagement;public class SelectPuzzleController : MonoBehaviour {    public void SelectPuzzle ()    {        string[] name = UnityEngine.EventSystems.EventSystem.current.currentSelectedGameObject.name.Split();        int index = int.Parse(name[1]);        if (GameManager.instance != null)        {            GameManager.instance.setIndex(index);        }        SceneManager.LoadScene("GamePlay");    }    public void BackToMenu ()    {        SceneManager.LoadScene("MainMenu");    }}